import React from 'react';
import axios from 'axios';

class UploadForm extends React.Component{
    constructor(props){
        super(props)
        this.onChangeFile = this.onChangeFile.bind(this)
        this.state = {
            fileChosen:null,
            loading:false
        }

    }
    onChangeFile = (e)=>{
        this.setState({fileChosen:e.target.file[0]})
    }
    onSubmitFile = ()=>{
        formFile = new FormData();
        formFile.append('new_file',this.state.fileChosen);

        axios.post('http://localhost:9696/upload',formFile)

        .then(function(res){
            console.log(res.data);
        })
        .catch(function(error){
            console.log(error);
        });

    }
    render(){
        return(
            <form id = "upload-form">
                <label for ="fileUpload">Upload</label>
                <input id = "fileUpload" onChangeText = {this.onChangeFile} type = "file"></input>
                <button type ="submit" onClick = {this.onChangeFile}></button>
            </form>
        )
    }

};
export default UploadForm;
